mapper.insert:
INSERT    								INTO   boardInfo
		 		(  
		 			boardId
		  		  , userEmail
		  		  , boardProfile
		  	      , boardWriter
		  	      , boardTitle
		  	      , boardDate
		          , boardContent
		 		 )


VALUES      	 (

                  #{boardId}
                 ,#{userEmail}
                 ,#{boardProfile}
                 ,#{boardWriter}
                 ,#{boardTitle}
                 ,sysdate()
                 ,#{boardContent}

                 )

mapper.update:

UPDATE  									boardInfo 
SET 		
       			   boardTitle   = #{boardTitle} 
        		 , boardContent = #{boardContent}
        		 , boardWriter   = #{boardWriter} 
        		 , boardProfile = #{boardProfile}
        		 , boardDate    = sysdate()


WHERE			   boardId   = #{boardId}	
  AND			   userEmail = #{userEmail}

 	UPDATE  									boardInfo 
	       SET 		
		       			   boardView = boardView + 1

		 WHERE			   boardId   = #{boardId}	
		   AND			   userEmail = #{userEmail}

mapper.delete:

DELETE  								        FROM    	boardInfo 


WHERE			   boardId   = #{boardId}	
  AND			   userEmail = #{userEmail}
