mapper.insert:
INSERT   		     		       INTO    commentInfo
		(
		
            boardId
 		   ,userEmail
 		   ,commentWriter
           ,commentProfile
           ,commentContent
		
		)
		
VALUES  (

             #{boardId}
 		   , #{userEmail}
 		   , #{commentWriter}
           , #{commentProfile}
           , #{commentContent}

		)
  ON DUPLICATE KEY UPDATE
  
            boardId         =  #{boardId}
 		   ,userEmail       =  #{userEmail}
 		   ,commentWriter   =  #{commentWriter}
           ,commentProfile  =  #{commentProfile}
           ,commentContent  =  #{commentContent}
