mapper.insert:
  
   
  
 	INSERT IGNORE 
 	        							INTO  searchList
            (
               searchNumber  
             , userEmail
             , bookTitle
             , searchAuthor
             , searchPublisher
             
             )
	 VALUES (
	          
	           #{searchNumber}  
	 		 , #{userEmail}
             , #{bookTitle}
             , #{searchAuthor}
             , #{searchPublisher}
             
	         )

mapper.update:
UPDATE                                  		  searchList 

   SET         searchNumber = searchNumber - 1

 WHERE         searchNumber = #{searchNumber}
         AND   userEmail    = #{userEmail}
