mapper.insert:
Insert into  userInfo (
						  UserImage
					    , UserEmail
					    , UserName
					    , UserPassword
					    , UserIntroduce
					    , UserDate 
					   
					   )
				 
values

      ( 	      		 
             #{image} 
          ,  #{email}
          ,  #{name} 
          ,  #{password}
          ,  #{introduce}
          ,  now()
       )

mapper.select:
SELECT UserEmail , UserPassword as password , UserName
FROM UserInfo
WHERE UserEmail = #{email}
