mapper.insert:

 INSERT                           INTO   noticeInfo
             (
                 boardId
               , userEmail
               , boardTitle
             )

VALUES       (     #{boardId}
                 , #{userEmail}
                 , #{boardTitle}
             )

mapper.update:
UPDATE							       noticeInfo
   SET     boardTitle = #{boardTitle}      
 WHERE     userEmail  = #{userEmail}
   AND     boardId    = #{boardId}

mapper.delete:
DELETE                                from  noticeInfo
 WHERE    userEmail = #{userEmail}
   AND    boardId   = #{boardId}
