mapper.select:
SELECT UserEmail as email , UserPassword as password , UserName as name
FROM UserInfo
WHERE UserEmail = #{email}

mapper.insert:
INSERT                                      	                                        
                                         INTO  userInfo 
			
					    (
						    UserImage
			  			  , UserEmail
		      			  , UserName
			  	   		  , UserPassword
			  			  , UserIntroduce
						  , UserDate 
					   
					    )
				 
VALUES

                        ( 	      		 
                            #{image} 
                          , #{email}
                          , #{name} 
                          , #{password}
                          , #{introduce}
                          , now()
                        )

mapper.update:
 	 UPDATE		        								UserInfo
    
    
    
     SET	
     				  UserName        = #{name}
    				, UserPassword    = #{password}
    				, UserIntroduce   = #{introduce}
    				
    
    		    	                               
                                      
     WHERE 
                   	  UserEmail = #{email}
